import React, { useEffect, useState } from "react";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
  import { Line } from 'react-chartjs-2';
import axios from "axios";
import moment from "moment-timezone";
  ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
  );


export default function CoinChart(props){
  const {id,currency,days}=props;
  const [coinval,setCoindata]=useState([]);
  
  useEffect(() => {
    const url = `https://api.coingecko.com/api/v3/coins/${id}/market_chart?vs_currency=${currency}&days=${days}`
    axios.get(url).then(
        (res) => { setCoindata(res.data);
        console.log(res.data) }               
    )
},[]);
console.log(coinval)
/*const coinChartData=coinval.prices.map(value=>({
  x:value[0],y:value[1].toLocalString().toFixed(2)
}));*/

    const options = {
        responsive: true,
        plugins: {
          legend: {
            position: 'top',
          },
          title: {
            display: true,
            text: `${id} Chart`,
          },
        },
      };
      
      
     const data = {
        labels:[1,2,3,4,5,6,7]
        /*coinChartData.map(value=>{
                  moment(value.x).format('MMMDD')
                })*/,
        datasets: [
          {
            label: `${id} in ${currency}`,
            data:[1,2,3,4,5,6,7],
            //coinChartData.map(value=>value.y),
            borderColor: 'rgb(255, 99, 132)',
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
          },
        ],
      };

    return(
        <>
          <Line options={options} data={data} />
        </>
    )

}
