import React, { useEffect, useState } from "react";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
  import { Line } from 'react-chartjs-2';
import axios from "axios";
import moment from "moment-timezone";
  ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
  );


export function CoinChart(props){

  const[coinval,setCoindata]=useState([]);
  
  useEffect(() => {
    const url = `https://api.coingecko.com/api/v3/coins/${props.id}/market_chart?vs_currency=${props.currency}&days=${props.days}`
    axios.get(url).then(
        (res) => { setCoindata(res.data)
        console.log(res.data) }               
    )
})
{/*const coinChartData=coinval.prices.map(value=>({
  x:value[0],y:value[1].toLocalString().toFixed(2)
}));*/
{/*coinChartData.map(value=>{
          moment(value.x).format('MMMDD')
        })*/}{/*coinChartData.map(value=>value.y)*/}}
    const options = {
        responsive: true,
        plugins: {
          legend: {
            position: 'top',
          },
          title: {
            display: true,
            text: `${props.id} Chart`,
          },
        },
      };
      
      
     const data = {
        labels:[1,2,3,4,5,6,7,8,9],
        datasets: [
          {
            label: `${props.id} in ${props.currency}`,
            data:[10,1,2,4,5,8,76,2,1],
            borderColor: 'rgb(255, 99, 132)',
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
          },
        ],
      };

    return(
        <>
          <Line options={options} data={data} />
        </>
    )

}
