import axios from 'axios'
import { useParams } from 'react-router-dom'
import React, { useState, useEffect } from 'react'
import DOMPurify from 'dompurify'
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
  import { Line } from 'react-chartjs-2';

import './Coin.css'
import CoinChart from '../components/Coinchart'


const Coin = () => {

    const params = useParams()
    const [coin, setCoin] = useState({});
    const [currency,setCurrency]=useState('usd');
    const [days,setDays]=useState('7');
    const[coinval,setCoindata]=useState(()=>{
        const coinUrl = `https://api.coingecko.com/api/v3/coins/${coin.id}/market_chart?vs_currency=usd&days=7`
    axios.get(coinUrl).then(
        (res) => { console.log(res.data)
        return res.data;
    })});
    const [data,setData]=useState({
        labels:[1,2,3,4,5,6,7]
        /*coinChartData.map(value=>{
                  moment(value.x).format('MMMDD')
                })*/,
        datasets: [
          {
            label: `${coin.id} in ${currency}`,
            data:[1,2,3,4,5,6,7],
            //coinChartData.map(value=>value.y),
            borderColor: 'rgb(255, 99, 132)',
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
          },
        ],
      })

    const url = `https://api.coingecko.com/api/v3/coins/${params.coinId}`


    useEffect(() => {
        axios.get(url).then((res) => {
            setCoin(res.data)
        }).catch((error) => {
            console.log(error)
        })
    },[])

    const handleCurrency=(e)=>{
        setCurrency(e.target.value);
        const coinUrl = `https://api.coingecko.com/api/v3/coins/${coin.id}/market_chart?vs_currency=${currency}&days=${days}`
        axios.get(coinUrl).then(
            (res) => { setCoindata(res.data)
            console.log(res.data);
        })
    }
    const handledays=(e)=>{
        setDays(e.target.value);
    }
    const coinChartData=coinval.prices.map(value=>({
        x:value[0],y:value[1].toLocalString().toFixed(2)
      }));

    const options = {
        responsive: true,
        plugins: {
          legend: {
            position: 'top',
          },
          title: {
            display: true,
            text: `${coin.id} Chart`,
          },
        },
      };


    return (
        <div>
            <div className='coin-container'>
                <div className='content'>
                    <h1>{coin.name}</h1>
                </div>
                <div className='content'>
                    <div className='rank'>
                        <span className='rank-btn'>Rank # {coin.market_cap_rank}</span>
                    </div>
                    <div className='info'>
                        <div className='coin-heading'>
                            {coin.image ? <img src={coin.image.small} alt='' /> : null}
                            <p>{coin.name}</p>
                            {coin.symbol ? <p>{coin.symbol.toUpperCase()}/USD</p> : null}

                        </div>
                        <div className='coin-price'>
                            {coin.market_data?.current_price ? <h1>${coin.market_data.current_price.usd.toLocaleString()}</h1> : null}
                        </div>
                    </div>
                </div>

                <div className='content'>
                    <select onChange={handleCurrency} value={currency}>
                        <option value="usd" >USD</option>
                        <option value="inr" >INR</option>
                    </select>
                    <select onChange={handledays} value={days}>
                        <option value="7" >7 days</option>
                        <option value="30" >1 Month</option>
                        <option value="182" >6 Months</option>
                        <option value="365" >1 Year</option>
                    </select>
                    <Line options={options} data={data} />

                </div>

                <div className='content'>

                    <table>
                        <thead>
                            <tr>
                                <th>1h</th>
                                <th>24h</th>
                                <th>7d</th>
                                <th>14d</th>
                                <th>30d</th>
                                <th>1yr</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>{coin.market_data?.price_change_percentage_1h_in_currency ? <p>{coin.market_data.price_change_percentage_1h_in_currency.usd.toFixed(1)}%</p> : null}</td>
                                <td>{coin.market_data?.price_change_percentage_24h_in_currency ? <p>{coin.market_data.price_change_percentage_24h_in_currency.usd.toFixed(1)}%</p> : null}</td>
                                <td>{coin.market_data?.price_change_percentage_24h_in_currency ? <p>{coin.market_data.price_change_percentage_7d_in_currency.usd.toFixed(1)}%</p> : null}</td>
