import React, { useEffect, useState } from "react";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
  import { Line } from 'react-chartjs-2';
import axios from "axios";
import moment from "moment-timezone";
import { useParams } from "react-router-dom";
  ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
  );


export default function CoinChart(){
  const params=useParams();
  const [currency,setCurrency]=useState('usd');
  const [days,setDays]=useState('7');
  const [coinval,setCoindata]=useState(/*()=>{
    const url = `https://api.coingecko.com/api/v3/coins/${id}/market_chart?vs_currency=${currency}&days=${days}`
    axios.get(url)
    .then((res)=>res.data);
  }*/);
  console.log(coinval);
  useEffect(() => {
    const url = `https://api.coingecko.com/api/v3/coins/${params.coinid}/market_chart?vs_currency=${currency}&days=${days}`
     axios.get(url).then(
        (res) => { setCoindata(res.data); }               
    )
},[id,currency,days]);

setTimeout(()=>{
  const coinChartData=coinval.prices.map(value=>({
    x:value[0],y:value[1].toFixed(2)
  }));
  console.log(coinChartData);
},5000)
  
  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: `${id} Chart`,
      },
    },
  };
  
  
 const data = {
    labels:[1,2,3,4,5,6,7],
    //coinChartData.map(value=>{ moment(value.x).format('MMMDD')}),
    datasets: [
      {
        label: `${id} in ${currency}`,
        data:[1,2,3,4,5,6,7],
        //coinChartData.map(value=>value.y),
        borderColor: 'rgb(255, 99, 132)',
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
      },
    ],
  };
  const handleCurrency=(e)=>{
    setCurrency(e.target.value);
    
}
const handledays=(e)=>{
    setDays(e.target.value);
}
  return(
    <>
      <select onChange={handleCurrency} value={currency}>
          <option value="usd" >USD</option>
          <option value="inr" >INR</option>
      </select>
      <select onChange={handledays} value={days}>
          <option value="7" >7 days</option>
          <option value="30" >1 Month</option>
          <option value="182" >6 Months</option>
          <option value="365" >1 Year</option>
      </select>
      <Line options={options} data={data} />
    </>
)

}
