import React, { useEffect, useState } from 'react'
import {Link} from 'react-router-dom'
import './Navbar.css'


const Navbar = () => {


    const [name,setName]=useState("UserName");
    const [email,setEmail]=useState('UserEmail');

    useEffect(()=>{
        fetch("http://localhost:8000/success-log",{
            method: "POST",
            headers:{
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                token:window.localStorage.getItem("token")
            })

        })
        .then((res)=>res.json())
        .then((data)=>{
            console.log(data,"userdata");
            setName(data.data.userid)
            setEmail(data.data.email);
        })
    })

    return (
        <>
            <div className='navbar'>

                <Link to='/home'><h1> Crypto <span className='purple'>Tracker</span></h1></Link>
                <nav>
                    <ul>      
                        <li><Link to='/home'>Home</Link></li>
                        <li><Link to="/Coins">Cryptocurrencies</Link></li>
                        <li><Link to='/wishlist'>Wishlist</Link></li>                  
                        <li>
                            <div className="user-det">
                                Hi {name} <i className="fa-solid fa-caret-down"></i>
                                {/*<span className='secon'>{email}</span>*/}
                                <div className='dropDown'>

                                </div>
                            </div>
                        </li>                    
                    </ul>
                </nav>

            </div>
        </>
    )
}

export default Navbar
