import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import ImgCard from "./cardTemp";
import "./home.css"
import Coin from "../routes/Coin";
import { Link } from "react-router-dom";


export default function Home(){

    const [search, setSearch] = useState('');
    const [data, setData] = useState([]);

    const handler = (e) => {
        setSearch(e.target.value);
    }

    useEffect(() => {
        const url = 'https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=50&page=1&sparkline=false'
        axios.get(url).then(
            (res) => { setData(res.data) }
                      
        )
    }, [])

    const filteredCoins = data.filter(data =>
        data.name.toLowerCase().includes(search.toLowerCase()))
    
    return(
        <>
        <Navbar/>
        <div className="search-header">
                <input value={search} onChange={handler} placeholder="Search for crypto" />
        </div>

            
            <div className="grid-container">

                {filteredCoins
                .map(coin =>{
                    return(
                    <Link to={`/coin/${coin.id}`} element={<Coin />} key={coin.id}>
                        <ImgCard
                            name={coin.name} rank={coin.market_cap_rank} price={coin.current_price}
                            marketCap={coin.market_cap} url={coin.image}
                        />
                    </Link>)
                })}
            </div>
            
        </>
    )
}
